adow="%5B%7B%22x_offset%22%3A%220%22%2C%22y_offset%22%3A%2210px%22%2C%22blur_radius%22%3A%2230px%22%2C%22shadow_color%22%3A%22rgba(0%2C0%2C0%2C0.08)%22%7D%5D"]Distribuzione dei fondi[/ld_fancy_heading][/vc_column][/vc_row][vc_row css=".vc_custom_1527857420004{padding-top: 130px !important;padding-bottom: 110px !important;background-color: #0a0b4e !important;}"][vc_column enable_content_animation="yes" ca_init_scale_x="1" ca_init_scale_y="1" ca_init_scale_z="1" ca_init_opacity="0" ca_an_scale_x="1" ca_an_scale_y="1" ca_an_scale_z="1" ca_an_opacity="1" offset="vc_col-md-offset-1 vc_col-md-3" ca_init_translate_y="30"][ld_fancy_heading tag="h6" transform="text-uppercase" use_custom_fonts_title="true" color="rgb(115, 255, 234)" fs="14px" lh="1.5em" ls="0.2em" fw="700"]A proposito[/ld_fancy_heading][vc_empty_space height="30px"][/vc_column][vc_column offset="vc_col-md-7"][vc_row_inner][vc_column_inner][/vc_column_inner][/vc_row_inner][vc_column_text]
